import {
    MantineProvider,
    Container,
    SimpleGrid,
    Title,
    Text,
    Card,
    Image,
    Button,
    Group,
    AppShell,
  } from '@mantine/core';
  import { Header } from '../../components/Header/Header';
  import { Footer } from '../../components/Footer';
  import { Sidebar } from '../../components/Sidebar';

  export default function HomePage() {
    const products = [
      {
        title: 'Sản phẩm 1',
        description: 'Mô tả chi tiết về Sản phẩm 1.',
        imageUrl: 'https://via.placeholder.com/150',
        price: '$29.99',
        link: '/product1',
      },
      {
        title: 'Sản phẩm 2',
        description: 'Mô tả chi tiết về Sản phẩm 2.',
        imageUrl: 'https://via.placeholder.com/150',
        price: '$39.99',
        link: '/product2',
      },
      {
        title: 'Sản phẩm 3',
        description: 'Mô tả chi tiết về Sản phẩm 3.',
        imageUrl: 'https://via.placeholder.com/150',
        price: '$49.99',
        link: '/product3',
      },
      {
        title: 'Sản phẩm 4',
        description: 'Mô tả chi tiết về Sản phẩm 4.',
        imageUrl: 'https://via.placeholder.com/150',
        price: '$59.99',
        link: '/product4',
      },
      {
        title: 'Sản phẩm 5',
        description: 'Mô tả chi tiết về Sản phẩm 5.',
        imageUrl: 'https://via.placeholder.com/150',
        price: '$69.99',
        link: '/product5',
      },
      {
        title: 'Sản phẩm 6',
        description: 'Mô tả chi tiết về Sản phẩm 6.',
        imageUrl: 'https://via.placeholder.com/150',
        price: '$79.99',
        link: '/product6',
      },
    ];

    return (
      <MantineProvider>
        <AppShell
          header={<Header />}
          navbar={<Sidebar />}
          footer={<Footer />}
        >
          <Container>
            <Title ta="center" mt="lg" order={1}>
              Chào Mừng Đến Với Cửa Hàng Của Chúng Tôi
            </Title>
            <Text ta="center" mt="md" size="lg" color="dimmed">
              Khám phá những sản phẩm mới nhất và ưu đãi hấp dẫn.
            </Text>

            <SimpleGrid cols={3} spacing="lg" mt="xl">
              {products.map((product) => (
                <Card shadow="sm" padding="lg" radius="md" withBorder key={product.title}>
                  <Card.Section>
                    <Image src={product.imageUrl} alt={product.title} height={160} />
                  </Card.Section>

                  <Text w={500} size="lg" mt="md">{product.title}</Text>
                  <Text size="sm" color="dimmed" mt="xs">{product.description}</Text>
                  <Text size="lg" color="blue" mt="xs">{product.price}</Text>

                  <Group mt="md">
                    <Button
                      variant="light"
                      color="blue"
                      radius="md"
                      component="a"
                      href={product.link}
                    >
                      Mua ngay
                    </Button>
                  </Group>
                </Card>
              ))}
            </SimpleGrid>
          </Container>
        </AppShell>
      </MantineProvider>
    );
  }
