import { MantineProvider, Container, SimpleGrid, Title, Text, Card, Image, Button, Group } from '@mantine/core';
import { CardItem } from '../../components/CardItem/CardItem';

export default function HomePage() {
  const services = [
    {
      title: 'Service 1',
      description: 'Detailed description about Service 1.',
      imageUrl: 'https://via.placeholder.com/150',
    },
    {
      title: 'Service 2',
      description: 'Detailed description about Service 2.',
      imageUrl: 'https://via.placeholder.com/150',
    },
    {
      title: 'Service 3',
      description: 'Detailed description about Service 3.',
      imageUrl: 'https://via.placeholder.com/150',
    },
  ];

  return (
    <MantineProvider>
      <Container>
        <Title ta="center" mt="lg" order={1}>Welcome to Our Website</Title>

        <Text ta="center" mt="md" size="lg" color="dimmed">
          Your one-stop solution for all your needs.
        </Text>

        <SimpleGrid cols={3} spacing="lg" mt="xl">
          {services.map((service) => (
            <Card shadow="sm" padding="lg" radius="md" withBorder key={service.title}>
              <Card.Section>
                <Image src={service.imageUrl} alt={service.title} height={160} />
              </Card.Section>

              <Text w={500} size="lg" mt="md">{service.title}</Text>
              <Text size="sm" color="dimmed" mt="xs">{service.description}</Text>

              <Group mt="md">
                <Button variant="light" color="blue" radius="md">
                  Learn More
                </Button>
              </Group>
            </Card>
          ))}
        </SimpleGrid>
      </Container>
    </MantineProvider>
  );
}
